import React, { useState, useEffect } from 'react';
import './AdminPanel.css';

const AdminPanel = () => {
  const [orders, setOrders] = useState([]);
  const [editingOrder, setEditingOrder] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [uploading, setUploading] = useState(false);
  const [uploadError, setUploadError] = useState('');
  const [uploadSuccess, setUploadSuccess] = useState('');
  const [selectedFile, setSelectedFile] = useState(null);

  // Rastgele tarih oluşturma fonksiyonu (3 hafta ile 1.5 ay arası)
  const getRandomPastDate = () => {
    const now = new Date();
    const threeWeeksAgo = new Date(now.getTime() - (21 * 24 * 60 * 60 * 1000));
    const sixWeeksAgo = new Date(now.getTime() - (42 * 24 * 60 * 60 * 1000));
    
    const randomTime = Math.random() * (threeWeeksAgo.getTime() - sixWeeksAgo.getTime()) + sixWeeksAgo.getTime();
    return new Date(randomTime);
  };

  // Başlangıç sipariş verilerini oluştur
  useEffect(() => {
    const initialOrders = [
      {
        id: 1,
        productName: "Domalma Am Parmaklama",
        customerName: "Arsz Sikici",
        email: "Dümdük9@email.com",
        quantity: 1,
        price: 18000,
        orderDate: getRandomPastDate(),
        status: "pending_payment",
        description: "Domalarak Am Parmaklama Popo Ayırma 5 Dakika Ayakta Am Parmaklama 5dk",
        notes: "Müşteri onay verirse ödeme yapılacak"
      },
      {
        id: 2,
        productName: "Meme Okşama",
        customerName: "Dümdük9",
        email: "Dümdük9@email.com",
        quantity: 2,
        price: 6950,
        orderDate: getRandomPastDate(),
        status: "pending_payment",
        description: "Öne Doğru Eğilerek Meme Okşama Efeksiz 5 Dakika",
        notes: "Müşteri onay verirse ödeme yapılacak"
      },
      {
        id: 3,
        productName: "Boşalma",
        customerName: "Cookiee",
        email: "Cookie82@email.com",
        quantity: 1,
        price: 13500,
        orderDate: getRandomPastDate(),
        status: "pending_payment",
        description: "Zevk Suyum Gelene Kadar Am Parmaklayarak Boşalma",
        notes: "Müşteri onay verirse ödeme yapılacak"
      }
    ];
    setOrders(initialOrders);
  }, []);

  // Tarih formatlama
  const formatDate = (date) => {
    return new Intl.DateTimeFormat('tr-TR', {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    }).format(date);
  };

  const handleFileChange = (e) => {
    const file = e.target.files?.[0] || null;
    setSelectedFile(file);
    setUploadError('');
    setUploadSuccess('');
  };

  const handleUpload = async (e) => {
    e.preventDefault();
    if (!selectedFile) {
      setUploadError('Lütfen bir video dosyası seçin.');
      return;
    }

    const allowedTypes = ['video/mp4', 'video/quicktime', 'video/webm'];
    if (!allowedTypes.includes(selectedFile.type)) {
      setUploadError('Sadece mp4, mov veya webm kabul edilir.');
      return;
    }

    const formData = new FormData();
    formData.append('file', selectedFile);

    try {
      setUploading(true);
      setUploadError('');
      setUploadSuccess('');
      const res = await fetch('/api/upload', {
        method: 'POST',
        body: formData
      });
      if (!res.ok) {
        const msg = await res.text();
        throw new Error(msg || 'Yükleme başarısız');
      }
      setUploadSuccess('Video Discord’a gönderildi.');
      setSelectedFile(null);
      // reset input value
      const input = document.getElementById('video-input');
      if (input) input.value = '';
    } catch (err) {
      setUploadError(err?.message || 'Bir hata oluştu');
    } finally {
      setUploading(false);
    }
  };

  // Sipariş düzenleme
  const handleEditOrder = (order) => {
    setEditingOrder({ ...order });
    setShowModal(true);
  };

  // Sipariş güncelleme
  const handleUpdateOrder = (updatedOrder) => {
    setOrders(orders.map(order => 
      order.id === updatedOrder.id ? updatedOrder : order
    ));
    setShowModal(false);
    setEditingOrder(null);
  };

  // Sipariş silme
  const handleDeleteOrder = (orderId) => {
    if (window.confirm('Bu siparişi silmek istediğinizden emin misiniz?')) {
      setOrders(orders.filter(order => order.id !== orderId));
    }
  };

  // Ödeme onayı
  const handleApprovePayment = (orderId) => {
    setOrders(orders.map(order => 
      order.id === orderId 
        ? { ...order, status: 'paid', notes: 'Sipariş gönderildi' }
        : order
    ));
  };

  return (
    <div className="admin-panel">
      <div className="admin-header">
        <h1>Admin Panel - Sipariş Yönetimi</h1>
        <div className="stats">
          <div className="stat-item">
            <span className="stat-number">{orders.length}</span>
            <span className="stat-label">Toplam Sipariş</span>
          </div>
          <div className="stat-item">
            <span className="stat-number">{orders.filter(o => o.status === 'pending_payment').length}</span>
            <span className="stat-label">Onay Bekleyen</span>
          </div>
        </div>
      </div>

      {/* Video Yükleme Alanı */}
      <div className="upload-card">
        <h2>Discord’a Video Gönder</h2>
        <form onSubmit={handleUpload} className="upload-form">
          <input
            id="video-input"
            type="file"
            accept="video/mp4,video/quicktime,video/webm"
            onChange={handleFileChange}
          />
          <button className="btn btn-approve" type="submit" disabled={uploading}>
            {uploading ? 'Gönderiliyor...' : 'Video Gönder'}
          </button>
        </form>
        {uploadError && <div className="upload-error">{uploadError}</div>}
        {uploadSuccess && <div className="upload-success">{uploadSuccess}</div>}
      </div>

      <div className="orders-container">
        {orders.map(order => (
          <div key={order.id} className="order-card">
            <div className="order-header">
              <h3>{order.productName}</h3>
              <span className={`status-badge ${order.status}`}>
                {order.status === 'pending_payment' ? 'Onay Bekleniyor' : 'Gönderildi'}
              </span>
            </div>
            
            <div className="order-details">
              <div className="detail-row">
                <strong>Müşteri:</strong> {order.customerName}
              </div>
              <div className="detail-row">
                <strong>Email:</strong> {order.email}
              </div>
              <div className="detail-row">
                <strong>Miktar:</strong> {order.quantity} adet
              </div>
              <div className="detail-row">
                <strong>Fiyat:</strong> {order.price.toLocaleString('tr-TR')} ₺
              </div>
              <div className="detail-row">
                <strong>Sipariş Tarihi:</strong> {formatDate(order.orderDate)}
              </div>
              <div className="detail-row">
                <strong>Açıklama:</strong> {order.description}
              </div>
              <div className="detail-row notes">
                <strong>Notlar:</strong> 
                <span className="red-note">{order.notes}</span>
              </div>
            </div>

            <div className="order-actions">
              <button
                className="btn btn-edit"
                onClick={() => handleEditOrder(order)}
              >
                Düzenle
              </button>
              <button 
                className="btn btn-delete" 
                onClick={() => handleDeleteOrder(order.id)}
              >
                Sil
              </button>
              {order.status === 'pending_payment' && (
                <button 
                  className="btn btn-approve" 
                  onClick={() => handleApprovePayment(order.id)}
                >
                  Gönder
                </button>
              )}
            </div>
          </div>
        ))}
      </div>

      {/* Düzenleme Modalı */}
      {showModal && editingOrder && (
        <EditOrderModal
          order={editingOrder}
          onSave={handleUpdateOrder}
          onClose={() => setShowModal(false)}
        />
      )}
    </div>
  );
};

// Düzenleme Modal Bileşeni
const EditOrderModal = ({ order, onSave, onClose }) => {
  const [formData, setFormData] = useState(order);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: name === 'price' || name === 'quantity' ? Number(value) : value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSave(formData);
  };

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <div className="modal-header">
          <h2>Sipariş Düzenle</h2>
          <button className="close-btn" onClick={onClose}>×</button>
        </div>
        
        <form onSubmit={handleSubmit} className="edit-form">
          <div className="form-group">
            <label>Ürün Adı:</label>
            <input
              type="text"
              name="productName"
              value={formData.productName}
              onChange={handleChange}
              required
            />
          </div>
          
          <div className="form-group">
            <label>Müşteri Adı:</label>
            <input
              type="text"
              name="customerName"
              value={formData.customerName}
              onChange={handleChange}
              required
            />
          </div>
          
          <div className="form-group">
            <label>Email:</label>
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              required
            />
          </div>
          
          
          <div className="form-group">
            <label>Miktar:</label>
            <input
              type="number"
              name="quantity"
              value={formData.quantity}
              onChange={handleChange}
              min="1"
              required
            />
          </div>
          
          <div className="form-group">
            <label>Fiyat:</label>
            <input
              type="number"
              name="price"
              value={formData.price}
              onChange={handleChange}
              min="0"
              required
            />
          </div>
          
          <div className="form-group">
            <label>Açıklama:</label>
            <textarea
              name="description"
              value={formData.description}
              onChange={handleChange}
              rows="3"
            />
          </div>
          
          <div className="form-group">
            <label>Notlar:</label>
            <textarea
              name="notes"
              value={formData.notes}
              onChange={handleChange}
              rows="2"
            />
          </div>
          
          <div className="form-actions">
            <button type="button" className="btn btn-cancel" onClick={onClose}>
              İptal
            </button>
            <button type="submit" className="btn btn-save">
              Kaydet
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default AdminPanel;
